mapper.select:

SELECT no, title, contents
FROM board
WHERE no=#{no }


SELECT no, title, contents, user_no AS userNo
FROM board
WHERE no = #{no };

mapper.update:


UPDATE board
SET title = #{title }, contents = #{contents }
WHERE no = #{no }

mapper.delete:

DELETE
FROM board
WHERE no = #{no } AND user_no = #{userNo }
